import React from 'react';
import './App.css';

function App() {

  var loadFile = function(event){
    var image = document.getElementById('output');
    image.src = URL.createObjectURL(event.target.files[0]);
  }

  var hSubmit = function handleSubmit(event){
    event.preventDefault();

    const data = new FormData(event.target);

    const value = Object.fromEntries(data.entries());

    const fileData = JSON.stringify(value);
    const blob = new Blob([fileData], {type: "text/plain"});
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.download = "test.json";
    link.href = url;
    link.click();

    console.log({value});
  }

  return (
    <div className="App">
      <form id="seizureForm">
      {/* label for the page*/}
      <p><label for="file"> Take Image </label></p>
      {/* <input accept='image/*' id="icon-button-file" type="file"
        capture="environment" /> */} 
        {/* This is the original method commented out */}
        {/*output element for the image uploaded*/} 
        <p>
        <label>Seizure Number: </label>
        <input type = "text"/>
        </p>
        <p>
        <label>Seizure Details:  </label>
        <textarea name="seizure-content" rows="2" cols="20"></textarea>
        </p>

        <p><img id="output" width="200"/></p>

        {/* upload box for the files, presented the option of using the main camera or the file explorer */}
        <input type="file" accept='image/*' name='image' id='file' onChange={[loadFile, hSubmit]} />
        <p>
          <input type = "submit" value = "Submit"/>
        </p>
        </form>
    </div>
  );
}

export default App;
